import React, {useState} from 'react'

export default function TextForm(props) {

    const handleOnClick = ()=>{
        console.log('handleUpClick function called');
        const newText = text.toUpperCase();
        setText(newText);
    }

    const handleOnChange = (event) => {
        console.log('Handle on click function called');
        setText(event.target.value);
    }
    const [text, setText] = useState('Enter text here...'); 

  return (

    <>
        <div className='container'>
            <h1>{props.title}</h1>
            <div className="mb-3">
                <label htmlFor="myText" className="form-label">Enter Message</label>
                <textarea className="form-control" value={text} id="myText" onChange={handleOnChange} rows="6"></textarea>
            </div>
            <button className='btn btn-primary' onClick={handleOnClick}>Convert</button>
        </div>
        <div className='container'>
            <h1>Text Summary</h1>
            <p>{text.split(' ').length} Words, {text.length} Charectors</p>
            <h2>Preview</h2>
            <p>{text}</p>
        </div>
    </>
  )
}
